package loopdemo;

public class doAndWhileDo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 20;
		while (n<10)
		{
			System.out.println(n);
			n++;
			}
		
		//do while
		
		int nx=10;
		do {
			System.out.println(nx);
			nx++;
		}while(nx<0);

	}

}
